// src/pages/Users/Users.js
import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Table } from "reactstrap";
import { blockUser, unblockUser, fetchUsers } from '../../redux/admin/actions';

const Users = () => {
  const dispatch = useDispatch();
  const users = useSelector(state => state.users);
  const loading = useSelector(state => state.loading);

  useEffect(() => {
    dispatch(fetchUsers()); // Fetch users on component mount
  }, [dispatch]);

  const handleBlock = (userId) => {
    dispatch(blockUser(userId));
  };

  const handleUnblock = (userId) => {
    dispatch(unblockUser(userId));
  };

  return (
    <div className="container-fluid">
      <h4>User Management</h4>
      <Table responsive>
        <thead className="text-primary">
          <tr>
            <th>Stt</th>
            <th>Id</th>
            <th>Name</th>
            <th>Status</th>
            <th>Date</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {loading ? (
            <tr><td colSpan="6">Loading...</td></tr>
          ) : (
            users.map((user, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{user.userId}</td>
                <td>{user.userName}</td>
                <td>{user.isActive ? 'Active' : 'Blocked'}</td>
                <td>{new Date(user.createdAt).toLocaleDateString()}</td>
                <td>
                  {user.isActive ? (
                    <button onClick={() => handleBlock(user.userId)} className="btn btn-danger">Block</button>
                  ) : (
                    <button onClick={() => handleUnblock(user.userId)} className="btn btn-success">Unblock</button>
                  )}
                </td>
              </tr>
            ))
          )}
        </tbody>
      </Table>
    </div>
  );
};

export default Users;
