import React, { useState } from 'react';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import { Table, Modal, ModalHeader, ModalBody, ModalFooter, Button } from "reactstrap";
import { thead, tbody, blockUser, dbBlockUser } from "../../variables/general";

const Users = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterDate, setFilterDate] = useState('');
  const [filteredMessages, setFilteredMessages] = useState(tbody);

  // State for modal visibility and user data
  const [modalOpen, setModalOpen] = useState(false);
  const [userToEdit, setUserToEdit] = useState(null);

  // Hàm để lọc tin nhắn dựa trên từ khóa và ngày
  const filterMessages = (term, date) => {
    const filtered = tbody.filter((message) => {
      const messageDate = new Date(message.date);
      const messageText = message.data.join(' ').toLowerCase();
      return (
        (!date || (!isNaN(messageDate) && messageDate.toISOString().slice(0, 10) === date)) &&
        messageText.includes(term)
      );
    });
    setFilteredMessages(filtered);
  };

  // Hàm xử lý tìm kiếm theo từ khóa
  const handleSearchChange = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);
    filterMessages(term, filterDate);
  };

  // Hàm lọc tin nhắn theo ngày
  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    setFilterDate(selectedDate);
    filterMessages(searchTerm, selectedDate);
  };

  // Hàm mở modal và set user cần chỉnh sửa
  const handleEditUser = (user) => {
    setUserToEdit({ ...user });
    setModalOpen(true);
  };

  // Hàm đóng modal
  const closeModal = () => {
    setModalOpen(false);
    setUserToEdit(null);
  };

  // Hàm lưu chỉnh sửa
  const handleSaveChanges = () => {
    console.log('Saving changes for user:', userToEdit);
    // Logic to save changes goes here
    closeModal();
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-3 p-0">
          <Sidebar />
        </div>
        <div className="col-md-9" style={{ marginLeft: '250px', width: '81.5%' }}>
          <Header title="Quản lý User" />
          <div className="container-fluid">
            <h4 style={{ margin: '10px' }}>Users Table</h4>

            {/* Search input */}
            <input
              type="text"
              placeholder="Tìm kiếm tin nhắn"
              value={searchTerm}
              onChange={handleSearchChange}
              className="form-control mb-3"
            />

            {/* Date filter */}
            <input
              type="date"
              value={filterDate}
              onChange={handleDateChange}
              className="form-control mb-3"
            />

            {/* Filtered users table */}
            <Table responsive>
              <thead className="text-primary">
                <tr>
                  {thead.map((prop, key) => (
                    <th key={key} className="text-center">{prop}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {filteredMessages.map((message, key) => (
                  <tr key={key}>
                    {message.data.map((field, key) => (
                      <td key={key} className="text-center">
                        {field}
                      </td>
                    ))}
                    <td className="text-center">
                      <button
                        className="ri ri-edit-2-line"
                        style={{ color: 'blue' }}
                        onClick={() => handleEditUser(message)} // Call handleEditUser with the current message
                      />
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>

            {/* Blocked Users table */}
            <h4 style={{ margin: '10px' }}>Blocked Users Table</h4>
            <Table responsive>
              <thead className="text-primary">
                <tr>
                  {blockUser.map((prop, key) => (
                    <th key={key} className="text-center">{prop}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {dbBlockUser.map((blockedUser, key) => (
                  <tr key={key}>
                    {blockedUser.data.map((field, key) => (
                      <td key={key} className="text-center">{field}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        </div>
      </div>

      {/* Modal Edit User */}
      <Modal isOpen={modalOpen} toggle={closeModal}>
        <ModalHeader toggle={closeModal}>Edit User</ModalHeader>
        <ModalBody>
          {userToEdit && (
            <div>
              <div>
                <label>Name:</label>
                <input
                  type="text"
                  value={userToEdit.data[2]} // Assuming Name is at index 2
                  onChange={(e) => setUserToEdit({
                    ...userToEdit,
                    data: [
                      userToEdit.data[0],
                      userToEdit.data[1],
                      e.target.value, // Update the Name
                      userToEdit.data[3],
                      userToEdit.data[4],
                      userToEdit.data[5]
                    ]
                  })}
                  className="form-control mb-3"
                />
              </div>
            </div>
          )}
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={handleSaveChanges}>Save Changes</Button>
          <Button color="secondary" onClick={closeModal}>Cancel</Button>
        </ModalFooter>
      </Modal>
    </div>
  );
};

export default Users;
