import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import { Table } from "reactstrap";
import { fetchUsers } from '../../redux/admin/actions'; // Import action to fetch users

const Users = () => {
  const dispatch = useDispatch();
  const { users, loading, error } = useSelector(state => state.user);

  useEffect(() => {
    dispatch(fetchUsers()); // Fetch users on component mount
  }, [dispatch]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-3 p-0">
          <Sidebar />
        </div>
        <div className="col-md-9" style={{ marginLeft: '250px', width: '81.5%' }}>
          <Header title="Quản lý User" />
          <div className="container-fluid">
            <h4 style={{ margin: '10px' }}>Users Table</h4>

            <Table responsive>
              <thead className="text-primary">
                <tr>
                  <th className="text-center">ID</th>
                  <th className="text-center">Name</th>
                  <th className="text-center">Status</th>
                  <th className="text-center">Action</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user, key) => (
                  <tr key={key}>
                    <td className="text-center">{user.id}</td>
                    <td className="text-center">{user.name}</td>
                    <td className="text-center">{user.isActive ? 'Active' : 'Blocked'}</td>
                    <td className="text-center">
                      {user.isActive ? (
                        <button onClick={() => dispatch(blockUser(user.userId))} className="btn btn-warning">Block</button>
                      ) : (
                        <button onClick={() => dispatch(unblockUser(user.userId))} className="btn btn-success">Unblock</button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Users;
