import React, { useState, useEffect } from 'react';

const Users = ({ showBlocked }) => {
  const [users, setUsers] = useState([]);  // Khởi tạo users là mảng rỗng
  const [blockedUsers, setBlockedUsers] = useState([]);  // Khởi tạo blockedUsers là mảng rỗng

  // Hàm lấy danh sách người dùng từ API
  useEffect(() => {
    const fetchUsers = async () => {
      const endpoint = showBlocked ? 'blocked' : 'unblocked';
      try {
        const response = await fetch(`https://localhost:7098/api/Admin/${endpoint}`, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
          }
        });
        
        const data = await response.json();
        
        if (response.ok) {
          // Kiểm tra và set dữ liệu phù hợp với điều kiện
          if (showBlocked) {
            setBlockedUsers(Array.isArray(data) ? data : []);  // Kiểm tra dữ liệu trả về có phải mảng không
          } else {
            setUsers(Array.isArray(data) ? data : []);  // Kiểm tra dữ liệu trả về có phải mảng không
          }
        } else {
          console.error('Error fetching users:', data);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchUsers();
  }, [showBlocked]);  // Khi showBlocked thay đổi, gọi lại API

  // Hàm mở modal cập nhật người dùng
  const openUpdateModal = (user) => {
    // Logic mở modal
  };

  // Hàm xử lý block/unblock người dùng
  const blockUserHandler = (userId) => {
    // Logic block người dùng
  };

  const unblockUserHandler = (userId) => {
    // Logic unblock người dùng
  };

  // Hàm xử lý xóa người dùng
  const deleteUserHandler = (userId) => {
    // Logic xóa người dùng
  };

  return (
    <div>
      <table className="table">
        <thead>
          <tr>
            <th className="text-center">STT</th>
            <th className="text-center">User ID</th>
            <th className="text-center">Full Name</th>
            <th className="text-center">Created At</th>
            <th className="text-center">Actions</th>
          </tr>
        </thead>
        <tbody>
          {/* Kiểm tra xem users có phải là mảng không trước khi gọi map */}
          {(Array.isArray(showBlocked ? blockedUsers : users) ? (showBlocked ? blockedUsers : users) : []).map((user, key) => (
            <tr key={key}>
              <td className="text-center">{key + 1}</td>
              <td className="text-center">{user.userId}</td>
              <td className="text-center">{user.fullName}</td>
              <td className="text-center">
                {new Date(user.createdAt).toLocaleDateString()}
              </td>
              <td className="text-center">
                <button
                  className="ri ri-edit-2-line"
                  style={{ color: 'black' }}
                  onClick={() => openUpdateModal(user)}
                />
                <button
                  className="ri ri-delete-bin-5-line"
                  style={{ color: 'red', marginLeft: '10px' }}
                  onClick={() => deleteUserHandler(user.userId)}
                />
                {showBlocked ? (
                  <button
                    className="ri ri-arrow-right-s-line"
                    style={{ marginLeft: '10px', color: 'green' }}
                    onClick={() => unblockUserHandler(user.userId)}
                  />
                ) : (
                  <button
                    className="ri ri-lock-line"
                    style={{ marginLeft: '10px', color: 'gray' }}
                    onClick={() => blockUserHandler(user.userId)}
                  />
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Users;
