import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux'; // Kết nối với Redux
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import { Table } from 'reactstrap';
import { fetchUsers } from '../../redux/admin/actions';

const Users = () => {
  const dispatch = useDispatch();
  const { users, loading, error } = useSelector((state) => state.user); // Lấy dữ liệu từ Redux store
  const [searchTerm, setSearchTerm] = useState('');
  const [filterDate, setFilterDate] = useState('');
  const [filteredUsers, setFilteredUsers] = useState([]);

  // Lọc người dùng theo từ khóa và ngày
  const filterUsers = (term, date) => {
    const filtered = users.filter((user) => {
      const userText = user.userName.toLowerCase() + user.email.toLowerCase();
      return (
        (!date || new Date(user.createdAt).toISOString().slice(0, 10) === date) &&
        userText.includes(term)
      );
    });
    setFilteredUsers(filtered);
  };

  // Hàm xử lý tìm kiếm theo từ khóa
  const handleSearchChange = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);
    filterUsers(term, filterDate); // Lọc lại theo từ khóa và ngày
  };

  // Hàm lọc người dùng theo ngày
  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    setFilterDate(selectedDate);
    filterUsers(searchTerm, selectedDate); // Lọc lại theo ngày và từ khóa
  };

  // Gọi hành động fetchUsers khi component load
  useEffect(() => {
    dispatch(fetchUsers());  // Gọi action fetchUsers từ Redux
  }, [dispatch]);

  // Hiển thị dữ liệu loading hoặc lỗi
  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-3 p-0">
          <Sidebar />
        </div>
        <div className="col-md-9" style={{ marginLeft: '250px', width: '81.5%' }}>
          <Header title="Quản lý User" />
          <div className="container-fluid">
            <h4 style={{ margin: '10px' }}>Users Table</h4>

            {/* Trường nhập liệu tìm kiếm từ khóa */}
            <input
              type="text"
              placeholder="Tìm kiếm người dùng"
              value={searchTerm}
              onChange={handleSearchChange}
              className="form-control mb-3"
            />

            {/* Bộ lọc theo ngày */}
            <input
              type="date"
              value={filterDate}
              onChange={handleDateChange}
              className="form-control mb-3"
            />

            {/* Bảng người dùng đã lọc */}
            <Table responsive>
              <thead className="text-primary">
                <tr>
                  {["Stt", "Id", "Name", "Status", "Date", "Action"].map((prop, key) => (
                    <th key={key} className="text-center">{prop}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {filteredUsers.map((user, key) => (
                  <tr key={key}>
                    <td className="text-center">{key + 1}</td>
                    <td className="text-center">{user.userId}</td>
                    <td className="text-center">{user.userName}</td>
                    <td className="text-center">
                      <div className={`Status_${user.isActive ? "Online" : "Offline"}`}>
                        {user.isActive ? "online" : "offline"}
                      </div>
                    </td>
                    <td className="text-center">{new Date(user.createdAt).toLocaleDateString()}</td>
                    <td className="text-center">
                      <div>
                        <button className="ri ri-edit-2-line" style={{ color: 'blue' }}></button>
                        <button className="ri ri-delete-bin-2-line" style={{ color: 'red' }}></button>
                        <button className="ri ri-close-circle-line" style={{ color: 'red' }}></button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Users;
