import React, { useState, useEffect } from 'react';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import { Table } from "reactstrap";
import { thead, blockUser, dbBlockUser } from "../../variables/general";

const Users = () => {
  const [users, setUsers] = useState([]);  // State to store user data
  const [searchTerm, setSearchTerm] = useState('');
  const [filterDate, setFilterDate] = useState('');
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [blockedUsers, setBlockedUsers] = useState([]);

  // Fetch users data from API when the component mounts
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch('https://localhost:7098/api/User/users', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`  // Assuming token is stored in localStorage
          }
        });
        const data = await response.json();
        if (response.ok) {
          setUsers(data);  // Set the users data into the state
          setFilteredUsers(data);  // Set the filtered users data initially
        } else {
          console.error('Error fetching users:', data);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchUsers();
  }, []);  // Empty dependency array ensures this runs only once when the component mounts

  // Function to filter users based on search term and date
  const filterUsers = (term, date) => {
    const filtered = users.filter((user) => {
      const userDate = new Date(user.createdAt);  // Assuming 'createdAt' field exists
      const userText = `${user.fullName} ${user.email}`.toLowerCase();  // You can customize which fields to include for searching
      return (
        (!date || (userDate.toISOString().slice(0, 10) === date)) &&
        userText.includes(term)
      );
    });
    setFilteredUsers(filtered);
  };

  // Handle search input change
  const handleSearchChange = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);
    filterUsers(term, filterDate);
  };

  // Handle date filter change
  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    setFilterDate(selectedDate);
    filterUsers(searchTerm, selectedDate);
  };

  // Function to block a user
  const blockUserHandler = async (userId) => {
    try {
      const response = await fetch(`https://localhost:7098/api/User/block/${userId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        // Update the blocked users and users list
        setBlockedUsers(prev => [...prev, users.find(user => user.userId === userId)]);
        setUsers(prev => prev.filter(user => user.userId !== userId));  // Remove from users list
      } else {
        console.error('Error blocking user:', data);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Function to unblock a user
  const unblockUserHandler = async (userId) => {
    try {
      const response = await fetch(`https://localhost:7098/api/User/unblock/${userId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        // Update the unblocked users and blocked users list
        setUsers(prev => [...prev, blockedUsers.find(user => user.userId === userId)]);
        setBlockedUsers(prev => prev.filter(user => user.userId !== userId));  // Remove from blocked list
      } else {
        console.error('Error unblocking user:', data);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-3 p-0">
          <Sidebar />
        </div>
        <div className="col-md-9" style={{ marginLeft: '250px', width: '81.5%' }}>
          <Header title="Quản lý User" />
          <div className="container-fluid">
            <h4 style={{ margin: '10px' }}>Users Table</h4>

            {/* Search input field */}
            <input
              type="text"
              placeholder="Tìm kiếm người dùng"
              value={searchTerm}
              onChange={handleSearchChange}
              className="form-control mb-3"
            />

            {/* Date filter */}
            <input
              type="date"
              value={filterDate}
              onChange={handleDateChange}
              className="form-control mb-3"
            />

            {/* Users Table */}
            <Table responsive>
              <thead className="text-primary">
                <tr>
                  {thead.map((prop, key) => (
                    <th key={key} className="text-center">{prop}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {filteredUsers.map((user, key) => (
                  <tr key={key}>
                    <td className="text-center">{key + 1}</td>
                    <td className="text-center">{user.userId}</td>
                    <td className="text-center">{user.fullName}</td>
                    <td className="text-center">
                      {user.isActive ? (
                        <span className='Status_Online'>Online</span>
                      ) : (
                        <span className='Status_Offline'>Offline</span>
                      )}
                    </td>
                    <td className="text-center">{new Date(user.createdAt).toLocaleDateString()}</td>
                    <td className="text-center">
                      <button 
                        className="ri ri-edit-2-line" 
                        style={{ color: 'blue' }} 
                      ></button>
                      <button 
                        className="ri ri-delete-bin-2-line" 
                        style={{ color: 'red' }}
                      ></button>
                      <button 
                        className="ri ri-close-circle-line" 
                        style={{ color: 'red' }}
                        onClick={() => blockUserHandler(user.userId)}  // Block the user
                      ></button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>

            {/* Blocked Users Table */}
            <h4 style={{ margin: '10px' }}>Blocked Users Table</h4>
            <Table responsive>
              <thead className="text-primary">
                <tr>
                  {blockUser.map((prop, key) => (
                    <th key={key} className="text-center">{prop}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {blockedUsers.map((blockedUser, key) => (
                  <tr key={key}>
                    <td className="text-center">{blockedUser.userId}</td>
                    <td className="text-center">{blockedUser.fullName}</td>
                    <td className="text-center">{new Date(blockedUser.createdAt).toLocaleDateString()}</td>
                    <td className="text-center">
                      <button 
                        className="ri ri-check-line" 
                        style={{ color: 'green' }}
                        onClick={() => unblockUserHandler(blockedUser.userId)} // Unblock the user
                      ></button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Users;
