import React, { useState, useEffect } from 'react';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import { Table, Modal, ModalHeader, ModalBody, ModalFooter, Button, Input } from "reactstrap";

const Users = () => {
  const [users, setUsers] = useState([]);
  const [blockedUsers, setBlockedUsers] = useState([]);
  const [showBlocked, setShowBlocked] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [updatedData, setUpdatedData] = useState({ fullName: '', email: '', role: '' });
  const [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    const fetchUsers = async () => {
      const endpoint = showBlocked ? 'blocked' : 'unblocked';
      try {
        const response = await fetch(`https://localhost:7098/api/User/${endpoint}`, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        const data = await response.json();
        if (response.ok) {
          if (showBlocked) {
            setBlockedUsers(data);
          } else {
            setUsers(data);
          }
        } else {
          console.error('Error fetching users:', data);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchUsers();
  }, [showBlocked]);

  const openUpdateModal = (user) => {
    setSelectedUser(user);
    setUpdatedData({ fullName: user.fullName, email: user.email, role: user.role });
    setModalOpen(true);
  };

  const closeUpdateModal = () => {
    setModalOpen(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedData(prev => ({ ...prev, [name]: value }));
  };

  const updateUserHandler = async () => {
    try {
      const response = await fetch(`https://localhost:7098/api/User/update/${selectedUser.userId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedData),
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        // Update UI after successful update
        const updatedUsers = users.map(user =>
          user.userId === selectedUser.userId ? { ...user, ...updatedData } : user
        );
        const updatedBlockedUsers = blockedUsers.map(user =>
          user.userId === selectedUser.userId ? { ...user, ...updatedData } : user
        );
        setUsers(updatedUsers);
        setBlockedUsers(updatedBlockedUsers);
        setModalOpen(false);
      } else {
        console.error('Error updating user:', data);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-3 p-0">
          <Sidebar />
        </div>
        <div className="col-md-9" style={{ marginLeft: '250px', width: '81.5%' }}>
          <Header title="Quản lý User" />
          <div className="container-fluid">
            <h4 style={{ margin: '10px' }}>Users Table</h4>

            {/* Toggle Button */}
            <button onClick={() => setShowBlocked(!showBlocked)} className="btn btn-primary mb-3">
              {showBlocked ? 'Show Unblocked Users' : 'Show Blocked Users'}
            </button>

            {/* Users Table */}
            <Table responsive>
              <thead className="text-primary">
                <tr>
                  <th className="text-center">#</th>
                  <th className="text-center">User ID</th>
                  <th className="text-center">Full Name</th>
                  <th className="text-center">Email</th>
                  <th className="text-center">Role</th>
                  <th className="text-center">Created At</th>
                  <th className="text-center">Action</th>
                </tr>
              </thead>
              <tbody>
                {showBlocked
                  ? blockedUsers.map((blockedUser, key) => (
                      <tr key={key}>
                        <td>{key + 1}</td>
                        <td>{blockedUser.userId}</td>
                        <td>{blockedUser.fullName}</td>
                        <td>{blockedUser.email}</td>
                        <td>{blockedUser.role}</td>
                        <td>{new Date(blockedUser.createdAt).toLocaleDateString()}</td>
                        <td>
                          <button onClick={() => openUpdateModal(blockedUser)} className="btn btn-info">Update</button>
                        </td>
                      </tr>
                    ))
                  : users.map((user, key) => (
                      <tr key={key}>
                        <td>{key + 1}</td>
                        <td>{user.userId}</td>
                        <td>{user.fullName}</td>
                        <td>{user.email}</td>
                        <td>{user.role}</td>
                        <td>{new Date(user.createdAt).toLocaleDateString()}</td>
                        <td>
                          <button onClick={() => openUpdateModal(user)} className="btn btn-info">Update</button>
                        </td>
                      </tr>
                    ))}
              </tbody>
            </Table>
          </div>

          {/* Update User Modal */}
          <Modal isOpen={modalOpen} toggle={closeUpdateModal}>
            <ModalHeader toggle={closeUpdateModal}>Update User</ModalHeader>
            <ModalBody>
              <div className="form-group">
                <label>Full Name</label>
                <Input
                  type="text"
                  name="fullName"
                  value={updatedData.fullName}
                  onChange={handleInputChange}
                />
              </div>
              <div className="form-group">
                <label>Email</label>
                <Input
                  type="email"
                  name="email"
                  value={updatedData.email}
                  onChange={handleInputChange}
                />
              </div>
              <div className="form-group">
                <label>Role</label>
                <Input
                  type="text"
                  name="role"
                  value={updatedData.role}
                  onChange={handleInputChange}
                />
              </div>
            </ModalBody>
            <ModalFooter>
              <Button color="primary" onClick={updateUserHandler}>
                Save Changes
              </Button>{' '}
              <Button color="secondary" onClick={closeUpdateModal}>Cancel</Button>
            </ModalFooter>
          </Modal>
        </div>
      </div>
    </div>
  );
};

export default Users;
