// src/pages/admin/Users.js
import React from 'react';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import { Table } from 'react-bootstrap';  // Nếu bạn sử dụng react-bootstrap

const Users = () => {
  return (
    
    <div className="container-fluid">
      <div className="row">
        {/* Sidebar */}
        <div className="col-md-3 p-0">
          <Sidebar />
        </div>
        
        {/* Main Content */}
        <div className="col-md-9" style={{ marginLeft: '250px', width:'81.5%'}}>
          <Header title="Quản lý User"/>
          <div className="container-fluid">
          <Table responsive>
                  <thead className="text-primary">
                    <tr>
                      {thead.map((prop, key) => {
                        if (key === thead.length - 1)
                          return (
                            <th key={key} className="text-center">
                              {prop}
                            </th>
                          );
                        return <th className="text-center" key={key}>{prop}</th>;
                      })}
                    </tr>
                  </thead>
                  <tbody>
                    {tbody.map((prop, key) => {
                      return (
                        <tr key={key}>
                          {prop.data.map((prop, key) => {
                            if (key === thead.length - 1)
                              return (
                                <td key={key} className="text-center">
                                  {prop}
                                </td>
                              );
                            return <td className="text-center" key={key}>{prop}</td>;
                          })}
                        </tr>
                      );
                    })}
                  </tbody>
                </Table>
              
            
                <Table responsive>
                  <thead className="text-primary">
                    <tr >
                      {thead.map((prop, key) => {
                        if (key === thead.length - 1)
                          return (
                            <th key={key} className="text-center">
                              {prop}
                            </th>
                          );
                        return <th className="text-center" key={key}>{prop}</th>;
                      })}
                    </tr>
                  </thead>
                  <tbody>
                    {blockUser.map((prop, key) => {
                      return (
                        <tr key={key}>
                          {prop.data.map((prop, key) => {
                            if (key === thead.length - 1)
                              return (
                                <td key={key} className="text-center">
                                  {prop}
                                </td>
                              );
                            return <td className="text-center" key={key}>{prop}</td>;
                          })}
                        </tr>
                      );
                    })}
                  </tbody>
                </Table>
        
          </div>
        </div>
      </div>
    </div>
  );
};

export default Users;
