import React, { useState } from 'react';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import {
  Table,
  Input,
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
} from 'reactstrap';

const Users = ({
  users,
  blockedUsers,
  onSearch,
  onDateFilter,
  onToggleBlocked,
  onBlockUser,
  onUnblockUser,
  onDeleteUser,
  onUpdateUser,
}) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterDate, setFilterDate] = useState({ start: '', end: '' });
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [updatedData, setUpdatedData] = useState({});

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    onSearch(e.target.value);
  };

  const handleDateChange = (e) => {
    const { name, value } = e.target;
    const newFilterDate = { ...filterDate, [name]: value };
    setFilterDate(newFilterDate);
    onDateFilter(newFilterDate);
  };

  const openUpdateModal = (user) => {
    setSelectedUser(user);
    setUpdatedData({
      fullName: user.fullName,
      email: user.email,
      role: user.role,
    });
    setModalOpen(true);
  };

  const handleUpdateChange = (e) => {
    const { name, value } = e.target;
    setUpdatedData((prev) => ({ ...prev, [name]: value }));
  };

  const handleUpdateSubmit = () => {
    onUpdateUser(selectedUser.userId, updatedData);
    setModalOpen(false);
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-3 p-0">
          <Sidebar />
        </div>
        <div className="col-md-9" style={{ marginLeft: '250px', width: '81.5%' }}>
          <Header title="User Management" />
          <div className="container-fluid">
            <h4 style={{ margin: '10px' }}>Users Table</h4>

            {/* Search Input */}
            <Input
              type="text"
              placeholder="Search Users"
              value={searchTerm}
              onChange={handleSearchChange}
              className="form-control mb-3"
            />

            {/* Date Filter */}
            <div className="d-flex mb-3">
              <Input
                type="date"
                name="start"
                value={filterDate.start}
                onChange={handleDateChange}
                className="form-control mr-2"
              />
              <span className="mx-2">to</span>
              <Input
                type="date"
                name="end"
                value={filterDate.end}
                onChange={handleDateChange}
                className="form-control ml-2"
              />
            </div>

            {/* Toggle Blocked Users */}
            <Button className="btn btn-info mb-3" onClick={onToggleBlocked}>
              Toggle Blocked/Unblocked Users
            </Button>

            {/* Users Table */}
            <Table responsive className="table-striped">
              <thead className="text-primary">
                <tr>
                  <th className="text-center">#</th>
                  <th className="text-center">User ID</th>
                  <th className="text-center">Full Name</th>
                  <th className="text-center">Created At</th>
                  <th className="text-center">Action</th>
                </tr>
              </thead>
              <tbody>
  {(users || []).map((user, key) => (
    <tr key={key}>
      <td className="text-center">{key + 1}</td>
      <td className="text-center">{user.userId}</td>
      <td className="text-center">{user.fullName}</td>
      <td className="text-center">
        {new Date(user.createdAt).toLocaleDateString()}
      </td>
      <td className="text-center">
        <Button
          color="primary"
          size="sm"
          className="mr-2"
          onClick={() => openUpdateModal(user)}
        >
          Edit
        </Button>
        <Button
          color="danger"
          size="sm"
          className="mr-2"
          onClick={() => onDeleteUser(user.userId)}
        >
          Delete
        </Button>
        <Button
          color={blockedUsers.includes(user) ? 'success' : 'warning'}
          size="sm"
          onClick={() =>
            blockedUsers.includes(user)
              ? onUnblockUser(user.userId)
              : onBlockUser(user.userId)
          }
        >
          {blockedUsers.includes(user) ? 'Unblock' : 'Block'}
        </Button>
      </td>
    </tr>
  ))}
</tbody>
            </Table>

            {/* Update User Modal */}
            {modalOpen && (
              <Modal isOpen={modalOpen} toggle={() => setModalOpen(false)}>
                <ModalHeader toggle={() => setModalOpen(false)}>
                  Update User
                </ModalHeader>
                <ModalBody>
                  <div className="form-group">
                    <label>Full Name</label>
                    <Input
                      type="text"
                      name="fullName"
                      value={updatedData.fullName}
                      onChange={handleUpdateChange}
                      className="form-control"
                    />
                  </div>
                  <div className="form-group">
                    <label>Email</label>
                    <Input
                      type="email"
                      name="email"
                      value={updatedData.email}
                      onChange={handleUpdateChange}
                      className="form-control"
                    />
                  </div>
                  <div className="form-group">
                    <label>Role</label>
                    <Input
                      type="text"
                      name="role"
                      value={updatedData.role}
                      onChange={handleUpdateChange}
                      className="form-control"
                    />
                  </div>
                </ModalBody>
                <ModalFooter>
                  <Button color="primary" onClick={handleUpdateSubmit}>
                    Save
                  </Button>
                  <Button color="secondary" onClick={() => setModalOpen(false)}>
                    Cancel
                  </Button>
                </ModalFooter>
              </Modal>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Users;
