// src/pages/admin/AdminDashboard.js
import React, { useState, useEffect } from 'react';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';

const AdminDashboard = () => {
  const [totalUsers, setTotalUsers] = useState(100); // Số người đăng ký (ví dụ)
  const [activeUsers, setActiveUsers] = useState(20); // Số người hoạt động (ví dụ)

  // useEffect để giả lập việc lấy dữ liệu từ API
  useEffect(() => {
    // Fetch dữ liệu từ API nếu có
    // Ví dụ: fetchTotalUsers();
    // setTotalUsers(apiData.totalUsers);
    // setActiveUsers(apiData.activeUsers);
  }, []);

  return (
    <div className="container-fluid">
      <div className="row">
        {/* Sidebar */}
        <div className="col-md-3 p-0">
          <Sidebar />
        </div>

        {/* Main Content */}
        <div className="col-md-9" style={{ marginLeft: '250px', width: '81%' }}>
          <Header title="Admin Dashboard" />
          <div className="container-fluid mt-4">
            <div className="row">
              {/* Card Số Người Đăng Ký */}
              <div className="col-md-6 mb-4">
                <div className="card text-white bg-info shadow">
                  <div className="card-body">
                    <h5 className="card-title">Số Người Đăng Ký</h5>
                    <p className="card-text" style={{ fontSize: '2rem' }}>{totalUsers}</p>
                  </div>
                </div>
              </div>

              {/* Card Số Người Hoạt Động */}
              <div className="col-md-6 mb-4">
                <div className="card text-white bg-success shadow">
                  <div className="card-body">
                    <h5 className="card-title">Số Người Hoạt Động</h5>
                    <p className="card-text" style={{ fontSize: '2rem' }}>{activeUsers}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
