// src/redux/actions.js
import { FETCH_USERS, FETCH_USERS_SUCCESS, FETCH_USERS_FAILURE } from './actionTypes';

export const fetchUsers = () => {
  return async (dispatch) => {
    dispatch({ type: FETCH_USERS });
    try {
      const response = await fetch('http://localhost:7098/api/User/users');
      const data = await response.json();
      dispatch({ type: FETCH_USERS_SUCCESS, users: data });
    } catch (error) {
      dispatch({ type: FETCH_USERS_FAILURE, error: error.message });
    }
  };
};
