import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchUsersRequest, blockUserRequest, unblockUserRequest, deleteUserRequest } from './actions/userActions';

const UserList = () => {
  const dispatch = useDispatch();
  const { users, blockedUsers, loading, error } = useSelector(state => state.user);

  const [showBlocked, setShowBlocked] = useState(false);

  useEffect(() => {
    dispatch(fetchUsersRequest(showBlocked));
  }, [dispatch, showBlocked]);

  const handleBlockUser = (userId) => {
    dispatch(blockUserRequest(userId));
  };

  const handleUnblockUser = (userId) => {
    dispatch(unblockUserRequest(userId));
  };

  const handleDeleteUser = (userId) => {
    dispatch(deleteUserRequest(userId));
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  const usersToShow = showBlocked ? blockedUsers : users;

  return (
    <div>
      <h1>User Management</h1>
      <button onClick={() => setShowBlocked(!showBlocked)}>
        {showBlocked ? 'Show Active Users' : 'Show Blocked Users'}
      </button>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {usersToShow.map(user => (
            <tr key={user.userId}>
              <td>{user.fullName}</td>
              <td>{user.email}</td>
              <td>{user.status}</td>
              <td>
                {user.status === 'Blocked' ? (
                  <button onClick={() => handleUnblockUser(user.userId)}>Unblock</button>
                ) : (
                  <button onClick={() => handleBlockUser(user.userId)}>Block</button>
                )}
                <button onClick={() => handleDeleteUser(user.userId)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserList;
