import React, { Suspense } from 'react';
import { Routes as SwitchRoute, Route, Navigate } from 'react-router-dom';
import { useSelector } from 'react-redux'; // Import useSelector

// Import routes
import { authProtectedRoutes, publicRoutes, callProtectedRoutes, adminRoutes } from './routes';

// Import layouts
import NonAuthLayout from "../layouts/NonAuth";
import AuthLayout from "../layouts/AuthLayout/";
import AdminLayout from "../layouts/AdminLayout/AdminLayout"; // Import AdminLayout
import AdminRoutes from './adminRoutes/AdminRoutes'; // Import AdminRoutes

// Authentication Protected Route
const AuthProtected = ({ children }) => {
  const isAuthenticated = useSelector((state) => state.Auth.isAuthenticated); // Check authentication status

  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }

  return <>{children}</>;
};

/**
 * Main Route component
 */
const Routes = () => {
  return (
    <React.Fragment>
      <Suspense fallback={<div>Loading...</div>}>
        <SwitchRoute>
          {/* Public Routes */}
          {publicRoutes.map((route, idx) => (
            <Route
              path={route.path}
              element={<NonAuthLayout>{route.component}</NonAuthLayout>}
              key={idx}
            />
          ))}

          {/* Admin Routes */}
          <Route
            path="/admin/*"
            element={
              <AuthProtected>
                <AdminLayout>
                  <AdminRoutes />
                </AdminLayout>
              </AuthProtected>
            }
          />

          {/* Private/Auth Protected Routes */}
          {authProtectedRoutes.map((route, idx) => (
            <Route
              path={route.path}
              element={
                <AuthProtected>
                  <AuthLayout>{route.component}</AuthLayout>
                </AuthProtected>
              }
              key={idx}
            />
          ))}
        </SwitchRoute>
      </Suspense>
    </React.Fragment>
  );
};

export default Routes;
