import React, { useState } from 'react';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import { Table, Button, Modal, ModalHeader, ModalBody, ModalFooter, Form, FormGroup, Label, Input } from "reactstrap";
import { thead, tbody, blockUser, dbBlockUser } from "../../variables/general";

const Users = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterDate, setFilterDate] = useState('');
  const [filteredMessages, setFilteredMessages] = useState(tbody);
  const [editingMessage, setEditingMessage] = useState(null); // State để lưu tin nhắn đang được chỉnh sửa
  const [isModalOpen, setIsModalOpen] = useState(false); // State để mở/đóng modal

  // Hàm để lọc tin nhắn dựa trên từ khóa và ngày
  const filterMessages = (term, date) => {
    const filtered = tbody.filter((message) => {
      const messageDate = new Date(message.date);
      const messageText = message.data.join(' ').toLowerCase();
      return (
        (!date || (!isNaN(messageDate) && messageDate.toISOString().slice(0, 10) === date)) &&
        messageText.includes(term)
      );
    });
    setFilteredMessages(filtered);
  };

  // Hàm xử lý tìm kiếm theo từ khóa
  const handleSearchChange = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);
    filterMessages(term, filterDate);
  };

  // Hàm lọc tin nhắn theo ngày
  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    setFilterDate(selectedDate);
    filterMessages(searchTerm, selectedDate);
  };

  // Hàm xử lý chỉnh sửa khi nhấn vào nút edit
  const handleEditClick = (message) => {
    setEditingMessage(message);
    setIsModalOpen(true); // Mở modal chỉnh sửa
  };

  // Hàm lưu thông tin khi nhấn "Lưu"
  const handleSaveEdit = () => {
    const updatedMessages = filteredMessages.map((message) =>
      message === editingMessage ? { ...message, data: editingMessage.data } : message
    );
    setFilteredMessages(updatedMessages);
    setIsModalOpen(false); // Đóng modal
    setEditingMessage(null); // Xóa trạng thái chỉnh sửa
  };

  // Hàm thay đổi thông tin khi chỉnh sửa
  const handleFieldChange = (e, index) => {
    const updatedMessage = { ...editingMessage };
    updatedMessage.data[index] = e.target.value;
    setEditingMessage(updatedMessage); // Cập nhật giá trị chỉnh sửa
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-3 p-0">
          <Sidebar />
        </div>
        <div className="col-md-9" style={{ marginLeft: '250px', width: '81.5%' }}>
          <Header title="Quản lý User" />
          <div className="container-fluid">
            <h4 style={{ margin: '10px' }}>Users Table</h4>

            {/* Trường nhập liệu tìm kiếm từ khóa */}
            <input
              type="text"
              placeholder="Tìm kiếm tin nhắn"
              value={searchTerm}
              onChange={handleSearchChange}
              className="form-control mb-3"
            />

            {/* Bộ lọc theo ngày */}
            <input
              type="date"
              value={filterDate}
              onChange={handleDateChange}
              className="form-control mb-3"
            />

            {/* Bảng tin nhắn đã lọc */}
            <Table responsive>
              <thead className="text-primary">
                <tr>
                  {thead.map((prop, key) => (
                    <th key={key} className="text-center">{prop}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {filteredMessages.map((message, key) => (
                  <tr key={key}>
                    {message.data.map((field, key) => (
                      <td key={key} className="text-center">
                        {key === 1 ? (
                          <span>{field}</span>
                        ) : (
                          <input
                            type="text"
                            value={field}
                            onChange={(e) => handleFieldChange(e, key)}
                            className="form-control"
                          />
                        )}
                      </td>
                    ))}
                    <td className="text-center">
                      <button
                        className="ri ri-edit-2-line"
                        style={{ color: 'blue' }}
                        onClick={() => handleEditClick(message)} // Gọi hàm chỉnh sửa
                      />
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>

            {/* Modal chỉnh sửa */}
            <Modal isOpen={isModalOpen} toggle={() => setIsModalOpen(!isModalOpen)}>
              <ModalHeader toggle={() => setIsModalOpen(!isModalOpen)}>Chỉnh sửa người dùng</ModalHeader>
              <ModalBody>
                {editingMessage && (
                  <Form>
                    <FormGroup>
                      <Label for="name">Name</Label>
                      <Input
                        type="text"
                        name="name"
                        id="name"
                        value={editingMessage.data[2]} // Lấy tên người dùng
                        onChange={(e) => handleFieldChange(e, 2)} // Cập nhật tên
                      />
                    </FormGroup>
                    <FormGroup>
                      <Label for="status">Status</Label>
                      <Input
                        type="text"
                        name="status"
                        id="status"
                        value={editingMessage.data[3]} // Lấy trạng thái
                        onChange={(e) => handleFieldChange(e, 3)} // Cập nhật trạng thái
                      />
                    </FormGroup>
                    {/* Thêm các trường khác nếu cần */}
                  </Form>
                )}
              </ModalBody>
              <ModalFooter>
                <Button color="primary" onClick={handleSaveEdit}>Lưu</Button>
                <Button color="secondary" onClick={() => setIsModalOpen(false)}>Hủy</Button>
              </ModalFooter>
            </Modal>

            {/* Bảng Blocked Users */}
            <h4 style={{ margin: '10px' }}>Blocked Users Table</h4>
            <Table responsive>
              <thead className="text-primary">
                <tr>
                  {blockUser.map((prop, key) => (
                    <th key={key} className="text-center">{prop}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {dbBlockUser.map((blockedUser, key) => (
                  <tr key={key}>
                    {blockedUser.data.map((field, key) => (
                      <td key={key} className="text-center">{field}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Users;
