import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Sidebar = () => {
  const [activeItem, setActiveItem] = useState('dashboard');

  const handleItemClick = (item) => {
    setActiveItem(item);
  };

  return (
    <aside className="sidebar">
      <div className="sidebar-header">
        <h2>Admin Dashboard</h2>
      </div>

      <ul className="sidebar-nav">
        <li className={activeItem === 'dashboard' ? 'active' : ''}>
          <Link to="/dashboard" onClick={() => handleItemClick('dashboard')}>
            <i className="icon-dashboard"></i>
            Dashboard
          </Link>
        </li>
        <li className={activeItem === 'users' ? 'active' : ''}>
          <Link to="/admin/users" onClick={() => handleItemClick('users')}>
            <i className="icon-users"></i>
            User Management
          </Link>
        </li>
        <li className={activeItem === 'groups' ? 'active' : ''}>
          <Link to="/admin/groups" onClick={() => handleItemClick('groups')}>
            <i className="icon-groups"></i>
            Groups
          </Link>
        </li>
        <li className={activeItem === 'messages' ? 'active' : ''}>
          <Link to="/admin/messages" onClick={() => handleItemClick('messages')}>
            <i className="icon-messages"></i>
            Messages
          </Link>
        </li>
      </ul>

      <div className="upgrade-to-pro">
        <button className="upgrade-button">Upgrade to Pro</button>
      </div>
    </aside>
  );
};

export default Sidebar;
