import React, { useState, useEffect } from 'react';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import { Table, Modal, ModalHeader, ModalBody, ModalFooter, Button, Input } from "reactstrap";

const Users = () => {
  const [users, setUsers] = useState([]); // State to store user data
  const [selectedUser, setSelectedUser] = useState(null); // State to store the selected user for editing
  const [updatedUserData, setUpdatedUserData] = useState({
    fullName: '',
    email: '',
    status: ''
  }); // State to store updated user data
  const [isModalOpen, setIsModalOpen] = useState(false); // State to control modal visibility

  // Fetch users data from API
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch('https://localhost:7098/api/User/unblocked', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}` // Assuming token is stored in localStorage
          }
        });
        const data = await response.json();
        if (response.ok) {
          setUsers(data);
        } else {
          console.error('Error fetching users:', data);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchUsers();
  }, []);

  // Open modal to edit user
  const openModal = (user) => {
    setSelectedUser(user);
    setUpdatedUserData({
      fullName: user.fullName,
      email: user.email,
      status: user.status
    });
    setIsModalOpen(true);
  };

  // Close modal
  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedUser(null);
    setUpdatedUserData({
      fullName: '',
      email: '',
      status: ''
    });
  };

  // Handle form input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedUserData({
      ...updatedUserData,
      [name]: value
    });
  };

  // Update user data on the server
  const updateUserHandler = async () => {
    if (!selectedUser) return;

    try {
      const response = await fetch(`https://localhost:7098/api/User/update/${selectedUser.userId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedUserData)  // Send updated user data in the request body
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        setUsers(prev => prev.map(user => 
          user.userId === selectedUser.userId ? { ...user, ...updatedUserData } : user
        ));
        closeModal(); // Close modal after updating
      } else {
        console.error('Error updating user:', data);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-3 p-0">
          <Sidebar />
        </div>
        <div className="col-md-9" style={{ marginLeft: '250px', width: '81.5%' }}>
          <Header title="Quản lý User" />
          <div className="container-fluid">
            <h4 style={{ margin: '10px' }}>Users Table</h4>

            <Table responsive>
              <thead className="text-primary">
                <tr>
                  <th className="text-center">#</th>
                  <th className="text-center">User ID</th>
                  <th className="text-center">Full Name</th>
                  <th className="text-center">Status</th>
                  <th className="text-center">Created At</th>
                  <th className="text-center">Action</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user, key) => (
                  <tr key={key}>
                    <td className="text-center">{key + 1}</td>
                    <td className="text-center">{user.userId}</td>
                    <td className="text-center">{user.fullName}</td>
                    <td className="text-center">
                      {user.isActive ? (
                        <span className="Status_Online">Online</span>
                      ) : (
                        <span className="Status_Offline">Offline</span>
                      )}
                    </td>
                    <td className="text-center">{new Date(user.createdAt).toLocaleDateString()}</td>
                    <td className="text-center">
                      <button
                        className="btn btn-primary"
                        onClick={() => openModal(user)}  // Open modal to edit user
                      >
                        Update
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        </div>
      </div>

      {/* Modal for updating user */}
      {selectedUser && (
        <Modal isOpen={isModalOpen} toggle={closeModal}>
          <ModalHeader toggle={closeModal}>Chỉnh sửa người dùng</ModalHeader>
          <ModalBody>
            <div>
              <label>Full Name</label>
              <Input
                type="text"
                name="fullName"
                value={updatedUserData.fullName}
                onChange={handleInputChange}
              />
            </div>
            <div>
              <label>Email</label>
              <Input
                type="email"
                name="email"
                value={updatedUserData.email}
                onChange={handleInputChange}
              />
            </div>
            <div>
              <label>Status</label>
              <Input
                type="text"
                name="status"
                value={updatedUserData.status}
                onChange={handleInputChange}
              />
            </div>
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={updateUserHandler}>
              Save Changes
            </Button>
            <Button color="secondary" onClick={closeModal}>
              Cancel
            </Button>
          </ModalFooter>
        </Modal>
      )}
    </div>
  );
};

export default Users;
