import React, { useState, useEffect } from 'react';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import { Table, Modal, ModalHeader, ModalBody, ModalFooter, Button, Input } from "reactstrap";

const Users = () => {
  const [users, setUsers] = useState([]);  // State to store user data
  const [searchTerm, setSearchTerm] = useState('');
  const [filterDate, setFilterDate] = useState('');
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [blockedUsers, setBlockedUsers] = useState([]);
  const [showBlocked, setShowBlocked] = useState(false);  // Toggle between blocked and unblocked
  const [selectedUser, setSelectedUser] = useState(null);  // Track selected user for updating
  const [updatedData, setUpdatedData] = useState({ fullName: '', email: '', role: '' });  // New data for update modal
  const [modalOpen, setModalOpen] = useState(false);  // Control modal visibility

  // Fetch users data from API when the component mounts or when showBlocked changes
  useEffect(() => {
    const fetchUsers = async () => {
      const endpoint = showBlocked ? 'blocked' : 'unblocked';  // Determine the endpoint based on showBlocked state
      try {
        const response = await fetch(`https://localhost:7098/api/User/${endpoint}`, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`  // Assuming token is stored in localStorage
          }
        });
        const data = await response.json();
        if (response.ok) {
          if (showBlocked) {
            setBlockedUsers(data);  // Set blocked users if showBlocked is true
          } else {
            setUsers(data);  // Set unblocked users if showBlocked is false
          }
        } else {
          console.error('Error fetching users:', data);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchUsers();
  }, [showBlocked]);  // Fetch when showBlocked changes

  // Function to block a user
  const blockUserHandler = async (userId) => {
    try {
      const response = await fetch(`https://localhost:7098/api/User/block/${userId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}` 
        }
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        setBlockedUsers(prev => [...prev, users.find(user => user.userId === userId)]);
        setUsers(prev => prev.filter(user => user.userId !== userId));  // Remove from users list
      } else {
        console.error('Error blocking user:', data);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Function to unblock a user
  const unblockUserHandler = async (userId) => {
    try {
      const response = await fetch(`https://localhost:7098/api/User/unblock/${userId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        setUsers(prev => [...prev, blockedUsers.find(user => user.userId === userId)]);
        setBlockedUsers(prev => prev.filter(user => user.userId !== userId));  // Remove from blocked list
      } else {
        console.error('Error unblocking user:', data);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Function to update a user
  const updateUserHandler = async (userId) => {
    try {
      const response = await fetch(`https://localhost:7098/api/User/update/${userId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedData),  // Send updated user data in the request body
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        setUsers(prev => prev.map(user => (user.userId === userId ? { ...user, ...updatedData } : user)));
        setBlockedUsers(prev => prev.map(user => (user.userId === userId ? { ...user, ...updatedData } : user)));
        setModalOpen(false);  // Close the modal after update
      } else {
        console.error('Error updating user:', data);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Function to delete a user
  const deleteUserHandler = async (userId) => {
    try {
      const response = await fetch(`https://localhost:7098/api/User/delete/${userId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        setUsers(prev => prev.filter(user => user.userId !== userId));
        setBlockedUsers(prev => prev.filter(user => user.userId !== userId));
      } else {
        console.error('Error deleting user:', data);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Toggle between showing blocked and unblocked users
  const toggleShowBlocked = () => {
    setShowBlocked(!showBlocked);
  };

  // Handle search input change
  const handleSearchChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    // Filter users based on search term
    if (showBlocked) {
      setBlockedUsers(prev => prev.filter(user => user.fullName.toLowerCase().includes(term.toLowerCase())));
    } else {
      setUsers(prev => prev.filter(user => user.fullName.toLowerCase().includes(term.toLowerCase())));
    }
  };

  // Handle date filter change
  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    setFilterDate(selectedDate);
    // Filter users based on selected date
    if (showBlocked) {
      setBlockedUsers(prev => prev.filter(user => new Date(user.createdAt).toLocaleDateString() === new Date(selectedDate).toLocaleDateString()));
    } else {
      setUsers(prev => prev.filter(user => new Date(user.createdAt).toLocaleDateString() === new Date(selectedDate).toLocaleDateString()));
    }
  };

  // Open update modal
  const openUpdateModal = (user) => {
    setSelectedUser(user);
    setUpdatedData({ fullName: user.fullName, email: user.email, role: user.role });
    setModalOpen(true);
  };

  // Close update modal
  const closeUpdateModal = () => {
    setModalOpen(false);
  };

  // Update input fields in the modal
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-3 p-0">
          <Sidebar />
        </div>
        <div className="col-md-9" style={{ marginLeft: '250px', width: '81.5%' }}>
          <Header title="Quản lý User" />
          <div className="container-fluid">
            <h4 style={{ margin: '10px' }}>Users Table</h4>

            {/* Search input field */}
            <input
              type="text"
              placeholder="Tìm kiếm người dùng"
              value={searchTerm}
              onChange={handleSearchChange}
              className="form-control mb-3"
            />

            {/* Date filter */}
            <input
              type="date"
              value={filterDate}
              onChange={handleDateChange}
              className="form-control mb-3"
            />

            {/* Toggle Button */}
            <button onClick={toggleShowBlocked} className="btn btn-primary mb-3">
              {showBlocked ? 'Show Unblocked Users' : 'Show Blocked Users'}
            </button>

            {/* Users Table */}
            <Table responsive>
              <thead className="text-primary">
                <tr>
                  <th className="text-center">#</th>
                  <th className="text-center">User ID</th>
                  <th className="text-center">Full Name</th>
                  <th className="text-center">Email</th>
                  <th className="text-center">Role</th>
                  <th className="text-center">Created At</th>
                  <th className="text-center">Action</th>
                </tr>
              </thead>
              <tbody>
                {showBlocked
                  ? blockedUsers.map((blockedUser, key) => (
                      <tr key={key}>
                        <td>{key + 1}</td>
                        <td>{blockedUser.userId}</td>
                        <td>{blockedUser.fullName}</td>
                        <td>{blockedUser.email}</td>
                        <td>{blockedUser.role}</td>
                        <td>{new Date(blockedUser.createdAt).toLocaleDateString()}</td>
                        <td>
                          <button onClick={() => unblockUserHandler(blockedUser.userId)} className="btn btn-warning">Unblock</button>
                          <button onClick={() => openUpdateModal(blockedUser)} className="btn btn-info">Update</button>
                          <button onClick={() => deleteUserHandler(blockedUser.userId)} className="btn btn-danger">Delete</button>
                        </td>
                      </tr>
                    ))
                  : users.map((user, key) => (
                      <tr key={key}>
                        <td>{key + 1}</td>
                        <td>{user.userId}</td>
                        <td>{user.fullName}</td>
                        <td>{user.email}</td>
                        <td>{user.role}</td>
                        <td>{new Date(user.createdAt).toLocaleDateString()}</td>
                        <td>
                          <button onClick={() => blockUserHandler(user.userId)} className="btn btn-warning">Block</button>
                          <button onClick={() => openUpdateModal(user)} className="btn btn-info">Update</button>
                          <button onClick={() => deleteUserHandler(user.userId)} className="btn btn-danger">Delete</button>
                        </td>
                      </tr>
                    ))}
              </tbody>
            </Table>
          </div>

          {/* Update User Modal */}
          <Modal isOpen={modalOpen} toggle={closeUpdateModal}>
            <ModalHeader toggle={closeUpdateModal}>Update User</ModalHeader>
            <ModalBody>
              <div className="form-group">
                <label>Full Name</label>
                <Input
                  type="text"
                  name="fullName"
                  value={updatedData.fullName}
                  onChange={handleInputChange}
                />
              </div>
              <div className="form-group">
                <label>Email</label>
                <Input
                  type="email"
                  name="email"
                  value={updatedData.email}
                  onChange={handleInputChange}
                />
              </div>
              <div className="form-group">
                <label>Role</label>
                <Input
                  type="text"
                  name="role"
                  value={updatedData.role}
                  onChange={handleInputChange}
                />
              </div>
            </ModalBody>
            <ModalFooter>
              <Button color="primary" onClick={() => updateUserHandler(selectedUser.userId)}>
                Save Changes
              </Button>{' '}
              <Button color="secondary" onClick={closeUpdateModal}>Cancel</Button>
            </ModalFooter>
          </Modal>
        </div>
      </div>
    </div>
  );
};

export default Users;
