import React, { useState, useEffect } from 'react';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import { Table, Modal, ModalHeader, ModalBody, ModalFooter, Input, Button } from 'reactstrap'; // Added missing imports
import { thead, blockUser, dbBlockUser } from "../../variables/general";

const Users = () => {
  const [users, setUsers] = useState([]);  // State to store user data
  const [searchTerm, setSearchTerm] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [blockedUsers, setBlockedUsers] = useState([]);
  const [showBlocked, setShowBlocked] = useState(false);  // Toggle between blocked and unblocked
  const [updatedData, setUpdatedData] = useState({ fullName: '', email: '', role: '' });
  const [selectedUser, setSelectedUser] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);

  // Open the modal to update user details
  const openUpdateModal = (user) => {
    setSelectedUser(user);
    setUpdatedData({
      fullName: user.fullName,
      email: user.email,
      role: user.role,
    });
    setModalOpen(true);
  };

  // Handle input changes for the update form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  // Fetch users data from API when the component mounts or when showBlocked changes
  useEffect(() => {
    const fetchUsers = async () => {
      const endpoint = showBlocked ? 'blocked' : 'unblocked';  // Determine the endpoint based on showBlocked state
      try {
        const response = await fetch(`https://localhost:7098/api/User/${endpoint}`, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`  // Assuming token is stored in localStorage
          }
        });
        const data = await response.json();
        if (response.ok) {
          if (showBlocked) {
            setBlockedUsers(data);  // Set blocked users if showBlocked is true
          } else {
            setUsers(data);  // Set unblocked users if showBlocked is false
          }
        } else {
          console.error('Error fetching users:', data);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchUsers();
  }, [showBlocked]);  // Fetch when showBlocked changes

  // Function to block a user
  const blockUserHandler = async (userId) => {
    try {
      const response = await fetch(`https://localhost:7098/api/User/block/${userId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}` 
        }
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        setBlockedUsers(prev => [...prev, users.find(user => user.userId === userId)]);
        setUsers(prev => prev.filter(user => user.userId !== userId));  // Remove from users list
      } else {
        console.error('Error blocking user:', data);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Function to unblock a user
  const unblockUserHandler = async (userId) => {
    try {
      const response = await fetch(`https://localhost:7098/api/User/unblock/${userId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        setUsers(prev => [...prev, blockedUsers.find(user => user.userId === userId)]);
        setBlockedUsers(prev => prev.filter(user => user.userId !== userId));  // Remove from blocked list
      } else {
        console.error('Error unblocking user:', data);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Function to update a user
  const updateUserHandler = async (userId) => {
    try {
      const response = await fetch(`https://localhost:7098/api/User/update/${userId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedData),  // Send updated user data in the request body
      });
  
      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        // Update both lists in the UI
        setUsers(prev => prev.map(user => (user.userId === userId ? { ...user, ...updatedData } : user)));
        setBlockedUsers(prev => prev.map(user => (user.userId === userId ? { ...user, ...updatedData } : user)));
        setModalOpen(false);  // Close the modal after update
      } else {
        console.error('Error updating user:', data);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Function to delete a user
  const deleteUserHandler = async (userId) => {
    try {
      const response = await fetch(`https://localhost:7098/api/User/delete/${userId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        setUsers(prev => prev.filter(user => user.userId !== userId));
        setBlockedUsers(prev => prev.filter(user => user.userId !== userId));
      } else {
        console.error('Error deleting user:', data);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Toggle between showing blocked and unblocked users
  const toggleShowBlocked = () => {
    setShowBlocked(!showBlocked);
  };

  // Handle search input change
  const handleSearchChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    // Filter users based on search term
    if (showBlocked) {
      setBlockedUsers(prev => prev.filter(user => user.fullName.toLowerCase().includes(term.toLowerCase())));
    } else {
      setUsers(prev => prev.filter(user => user.fullName.toLowerCase().includes(term.toLowerCase())));
    }
  };

  // Handle date range filter changes
  const handleStartDateChange = (e) => {
    const start = e.target.value;
    setStartDate(start);
    filterUsersByDate(start, endDate);
  };

  const handleEndDateChange = (e) => {
    const end = e.target.value;
    setEndDate(end);
    filterUsersByDate(startDate, end);
  };

  const filterUsersByDate = (start, end) => {
    const startDateObj = new Date(start);
    const endDateObj = new Date(end);
    if (showBlocked) {
      setBlockedUsers(prev => prev.filter(user => {
        const userDate = new Date(user.createdAt);
        return userDate >= startDateObj && userDate <= endDateObj;
      }));
    } else {
      setUsers(prev => prev.filter(user => {
        const userDate = new Date(user.createdAt);
        return userDate >= startDateObj && userDate <= endDateObj;
      }));
    }
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-3 p-0">
          <Sidebar />
        </div>
        <div className="col-md-9" style={{ marginLeft: '250px', width: '81.5%' }}>
          <Header title="Quản lý User" />
          <div className="container-fluid">
            <h4 style={{ margin: '10px' }}>Users Table</h4>

            {/* Search input field */}
            <input
              type="text"
              placeholder="Tìm kiếm người dùng"
              value={searchTerm}
              onChange={handleSearchChange}
              className="form-control mb-3"
            />

            {/* Start Date filter */}
            <input
              type="date"
              value={startDate}
              onChange={handleStartDateChange}
              className="form-control mb-3"
            />

            {/* End Date filter */}
            <input
              type="date"
              value={endDate}
              onChange={handleEndDateChange}
              className="form-control mb-3"
            />

            {/* Toggle Button */}
            <button onClick={toggleShowBlocked} className="btn btn-primary mb-3">
              {showBlocked ? 'Show Active Users' : 'Show Blocked Users'}
            </button>

            {/* Users Table */}
            <Table responsive>
              <thead>
                <tr>
                  {thead.map((header, index) => (
                    <th key={index}>{header}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {(showBlocked ? blockedUsers : users).map((user) => (
                  <tr key={user.userId}>
                    <td>{user.fullName}</td>
                    <td>{user.email}</td>
                    <td>{user.role}</td>
                    <td>{user.createdAt}</td>
                    <td>
                      {/* Block/Unblock */}
                      <button
                        onClick={() => (showBlocked ? unblockUserHandler(user.userId) : blockUserHandler(user.userId))}
                        className="btn btn-danger"
                      >
                        {showBlocked ? 'Unblock' : 'Block'}
                      </button>
                      {/* Edit */}
                      <button onClick={() => openUpdateModal(user)} className="btn btn-info">
                        Edit
                      </button>
                      {/* Delete */}
                      <button
                        onClick={() => deleteUserHandler(user.userId)}
                        className="btn btn-danger ml-2"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>

            {/* Modal for updating user */}
            <Modal isOpen={modalOpen} toggle={() => setModalOpen(false)}>
              <ModalHeader toggle={() => setModalOpen(false)}>Update User</ModalHeader>
              <ModalBody>
                <Input
                  type="text"
                  name="fullName"
                  value={updatedData.fullName}
                  onChange={handleInputChange}
                  placeholder="Full Name"
                  className="mb-2"
                />
                <Input
                  type="email"
                  name="email"
                  value={updatedData.email}
                  onChange={handleInputChange}
                  placeholder="Email"
                  className="mb-2"
                />
                <Input
                  type="text"
                  name="role"
                  value={updatedData.role}
                  onChange={handleInputChange}
                  placeholder="Role"
                  className="mb-2"
                />
              </ModalBody>
              <ModalFooter>
                <Button color="primary" onClick={() => updateUserHandler(selectedUser.userId)}>
                  Save
                </Button>
                <Button color="secondary" onClick={() => setModalOpen(false)}>
                  Cancel
                </Button>
              </ModalFooter>
            </Modal>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Users;
