import React, { useState, useEffect } from 'react';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import { Table } from "reactstrap";
import { thead, blockUser, dbBlockUser } from "../../variables/general";

const Users = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterDate, setFilterDate] = useState('');
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [blockedUsers, setBlockedUsers] = useState([]);
  const [showBlocked, setShowBlocked] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [updatedData, setUpdatedData] = useState({
    fullName: '',
    email: '',
    role: '',
  });

  useEffect(() => {
    const fetchUsers = async () => {
      const endpoint = showBlocked ? 'blocked' : 'unblocked';
      try {
        const response = await fetch(`https://localhost:7098/api/User/${endpoint}`, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        const data = await response.json();
        if (response.ok) {
          if (showBlocked) {
            setBlockedUsers(data);
          } else {
            setUsers(data);
          }
        } else {
          console.error('Error fetching users:', data);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchUsers();
  }, [showBlocked]);

  const handleUpdateClick = (user) => {
    setSelectedUser(user);
    setUpdatedData({
      fullName: user.fullName,
      email: user.email,
      role: user.role,
    });
  };

  const handleFieldChange = (e) => {
    const { name, value } = e.target;
    setUpdatedData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleUpdateSubmit = async (e) => {
    e.preventDefault();
    if (!selectedUser) return;

    try {
      const response = await fetch(`https://localhost:7098/api/User/update/${selectedUser.userId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedData),
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        setUsers(prev => prev.map(user => (user.userId === selectedUser.userId ? { ...user, ...updatedData } : user)));
        setBlockedUsers(prev => prev.map(user => (user.userId === selectedUser.userId ? { ...user, ...updatedData } : user)));
        setSelectedUser(null);
      } else {
        console.error('Error updating user:', data);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Handle search term change
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    const filtered = (showBlocked ? blockedUsers : users).filter(user =>
      user.fullName.toLowerCase().includes(e.target.value.toLowerCase()) ||
      user.email.toLowerCase().includes(e.target.value.toLowerCase())
    );
    setFilteredUsers(filtered);
  };

  // Handle date filter change
  const handleDateChange = (e) => {
    setFilterDate(e.target.value);
    const filtered = (showBlocked ? blockedUsers : users).filter(user =>
      new Date(user.createdAt).toLocaleDateString() === new Date(e.target.value).toLocaleDateString()
    );
    setFilteredUsers(filtered);
  };

  // Toggle the blocked users view
  const toggleShowBlocked = () => {
    setShowBlocked(prev => !prev);
  };

  // Unblock user handler
  const unblockUserHandler = async (userId) => {
    try {
      const response = await fetch(`https://localhost:7098/api/User/unblock/${userId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      if (response.ok) {
        alert('User unblocked successfully!');
        setShowBlocked(true);  // Refresh the blocked users list
      } else {
        console.error('Error unblocking user');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Delete user handler
  const deleteUserHandler = async (userId) => {
    try {
      const response = await fetch(`https://localhost:7098/api/User/delete/${userId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      if (response.ok) {
        alert('User deleted successfully!');
        setUsers(prev => prev.filter(user => user.userId !== userId));  // Remove deleted user from list
      } else {
        console.error('Error deleting user');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Block user handler
  const blockUserHandler = async (userId) => {
    try {
      const response = await fetch(`https://localhost:7098/api/User/block/${userId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      if (response.ok) {
        alert('User blocked successfully!');
        setShowBlocked(true);  // Refresh the blocked users list
      } else {
        console.error('Error blocking user');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-3 p-0">
          <Sidebar />
        </div>
        <div className="col-md-9" style={{ marginLeft: '250px', width: '81.5%' }}>
          <Header title="Quản lý User" />
          <div className="container-fluid">
            <h4 style={{ margin: '10px' }}>Users Table</h4>

            <input
              type="text"
              placeholder="Tìm kiếm người dùng"
              value={searchTerm}
              onChange={handleSearchChange}
              className="form-control mb-3"
            />

            <input
              type="date"
              value={filterDate}
              onChange={handleDateChange}
              className="form-control mb-3"
            />

            <button onClick={toggleShowBlocked} className="btn btn-primary mb-3">
              {showBlocked ? 'Show Unblocked Users' : 'Show Blocked Users'}
            </button>

            <Table responsive>
              <thead className="text-primary">
                <tr>
                  <th className="text-center">#</th>
                  <th className="text-center">User ID</th>
                  <th className="text-center">Full Name</th>
                  {!showBlocked && <th className="text-center">Status</th>}
                  <th className="text-center">Created At</th>
                  <th className="text-center">Action</th>
                </tr>
              </thead>
              <tbody>
                {showBlocked
                  ? blockedUsers.map((blockedUser, key) => (
                      <tr key={key}>
                        <td className="text-center">{key + 1}</td>
                        <td className="text-center">{blockedUser.userId}</td>
                        <td className="text-center">{blockedUser.fullName}</td>
                        <td className="text-center">{new Date(blockedUser.createdAt).toLocaleDateString()}</td>
                        <td className="text-center">
                          <button
                            className="ri ri-check-line"
                            style={{ color: 'green' }}
                            onClick={() => unblockUserHandler(blockedUser.userId)}
                          ></button>
                        </td>
                      </tr>
                    ))
                  : users.map((user, key) => (
                      <tr key={key}>
                        <td className="text-center">{key + 1}</td>
                        <td className="text-center">{user.userId}</td>
                        <td className="text-center">{user.fullName}</td>
                        <td className="text-center">
                          {user.isActive ? (
                            <span className="Status_Online">Online</span>
                          ) : (
                            <span className="Status_Offline">Offline</span>
                          )}
                        </td>
                        <td className="text-center">{new Date(user.createdAt).toLocaleDateString()}</td>
                        <td className="text-center">
                          <button
                            className="ri ri-lock-line"
                            style={{ color: 'gray' }}
                            onClick={() => blockUserHandler(user.userId)}
                          ></button>
                          <button
                            className="ri ri-delete-bin-line"
                            style={{ color: 'red' }}
                            onClick={() => deleteUserHandler(user.userId)}
                          ></button>
                        </td>
                      </tr>
                    ))}
              </tbody>
            </Table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Users;
