import React, { useState } from 'react';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import { Table } from "reactstrap";
import { thead, tbody, blockUser, dbBlockUser } from "../../variables/general";

const Users = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterDate, setFilterDate] = useState('');
  const [filteredMessages, setFilteredMessages] = useState(tbody);
  const [isEditing, setIsEditing] = useState(false); // Track if we're editing
  const [editingUser, setEditingUser] = useState(null); // The user currently being edited

  // Hàm để lọc tin nhắn dựa trên từ khóa và ngày
  const filterMessages = (term, date) => {
    const filtered = tbody.filter((message) => {
      const messageDate = new Date(message.date);
      const messageText = message.data.join(' ').toLowerCase();
      return (
        (!date || (!isNaN(messageDate) && messageDate.toISOString().slice(0, 10) === date)) &&
        messageText.includes(term)
      );
    });
    setFilteredMessages(filtered);
  };

  // Hàm xử lý tìm kiếm theo từ khóa
  const handleSearchChange = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);
    filterMessages(term, filterDate);
  };

  // Hàm lọc tin nhắn theo ngày
  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    setFilterDate(selectedDate);
    filterMessages(searchTerm, selectedDate);
  };

  // Hàm xử lý sự kiện chỉnh sửa user
  const handleEditUser = (user) => {
    setIsEditing(true);
    setEditingUser({ ...user }); // Set the user data to be edited
  };

  // Hàm lưu chỉnh sửa
  const handleSaveEdit = () => {
    // Update the filteredMessages state with the edited user data
    const updatedMessages = filteredMessages.map((message) =>
      message.data[1] === editingUser.data[1] ? editingUser : message
    );
    setFilteredMessages(updatedMessages);
    setIsEditing(false); // Exit edit mode
  };

  // Hàm hủy chỉnh sửa
  const handleCancelEdit = () => {
    setIsEditing(false); // Exit edit mode without saving
    setEditingUser(null);
  };

  // Hàm thay đổi dữ liệu khi chỉnh sửa
  const handleInputChange = (e, index) => {
    const updatedUser = { ...editingUser };
    updatedUser.data[index] = e.target.value;
    setEditingUser(updatedUser);
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-3 p-0">
          <Sidebar />
        </div>
        <div className="col-md-9" style={{ marginLeft: '250px', width: '81.5%' }}>
          <Header title="Quản lý User" />
          <div className="container-fluid">
            <h4 style={{ margin: '10px' }}>Users Table</h4>

            {/* Trường nhập liệu tìm kiếm từ khóa */}
            <input
              type="text"
              placeholder="Tìm kiếm tin nhắn"
              value={searchTerm}
              onChange={handleSearchChange}
              className="form-control mb-3"
            />

            {/* Bộ lọc theo ngày */}
            <input
              type="date"
              value={filterDate}
              onChange={handleDateChange}
              className="form-control mb-3"
            />

            {/* Bảng tin nhắn đã lọc */}
            <Table responsive>
              <thead className="text-primary">
                <tr>
                  {thead.map((prop, key) => (
                    <th key={key} className="text-center">{prop}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {filteredMessages.map((message, key) => (
                  <tr key={key}>
                    {message.data.map((field, fieldKey) => (
                      <td key={fieldKey} className="text-center">
                        {isEditing && editingUser && editingUser.data[1] === message.data[1] ? (
                          // Show input fields when editing
                          <input
                            type="text"
                            value={editingUser.data[fieldKey]}
                            onChange={(e) => handleInputChange(e, fieldKey)}
                          />
                        ) : (
                          field
                        )}
                      </td>
                    ))}
                    <td>
                      {isEditing && editingUser && editingUser.data[1] === message.data[1] ? (
                        <>
                          <button onClick={handleSaveEdit} className="btn btn-success">Save</button>
                          <button onClick={handleCancelEdit} className="btn btn-secondary">Cancel</button>
                        </>
                      ) : (
                        <button
                          onClick={() => handleEditUser(message)}
                          className="btn btn-primary"
                        >
                          Edit
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>

            {/* Bảng Blocked Users */}
            <h4 style={{ margin: '10px' }}>Blocked Users Table</h4>
            <Table responsive>
              <thead className="text-primary">
                <tr>
                  {blockUser.map((prop, key) => (
                    <th key={key} className="text-center">{prop}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {dbBlockUser.map((blockedUser, key) => (
                  <tr key={key}>
                    {blockedUser.data.map((field, key) => (
                      <td key={key} className="text-center">{field}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Users;
